import React, { useState, useEffect } from 'react';
import axios from 'axios';

function Vid({ videoId, Title }) {
    const [selectedVideo, setSelectedVideo] = useState(null);
    const [selectedSubtitle, setSubtitle] = useState([]);
    const displayTitle = Title || "None selected";

    useEffect(() => {
        const fetchVideoData = async () => {
            try {
                const response = await axios.get(`http://localhost:8000/play/${videoId}/`);
                setSelectedVideo(response.data.video_url);

                // Decode base64 subtitles and create Blob URLs
                const subtitles = response.data.subtitles.map(subtitle => {
                    const decodedSubtitle = atob(subtitle.subtitle_file);
                    const blob = new Blob([decodedSubtitle], { type: 'application/x-subrip' }); 
                    return {
                        language: subtitle.language,
                        subtitle_file_url: URL.createObjectURL(blob)
                    };
                });

                setSubtitle(subtitles);
            } catch (error) {
                console.error("Error fetching video data:", error);
            }
        };

        if (videoId) {
            fetchVideoData();
        }
    }, [videoId]);

    return (
        <div className="main_vid">
            {console.log(selectedVideo)}
            {console.log(selectedSubtitle)}
            {console.log(videoId)}
            <header className="title-playing">{displayTitle}</header>
            <div className="play_area me-2">
                <video className="play_area" width="1000" height="500" controls>
                    {selectedVideo && <source src={selectedVideo} type="video/mp4" />}
                    {selectedSubtitle.map((subtitle, index) => (
                        <track
                            key={index}
                            src={subtitle.subtitle_file_url}
                            label={subtitle.language}
                            kind="subtitles"
                            srclang={subtitle.language}
                            default={index === 0}
                        />
                    ))}
                    Your browser does not support the video tag.
                </video>
            </div>
        </div>
    );
}

export default Vid;
