import './App.css';
import Vid from './video_section';
import Search from './search';
import Vid_list from './vid_list';
import Subtitle from './subtitle_section';
import Navbar from './Navbar';
import { useState } from 'react';

function App() {
  const [enable, setEnable] = useState('');
  const [isOpen, setOpen] = useState('');
  const handleClose = () => {
    setOpen(false)
    setTimeout(() => {
      setEnable(false);
    }, 500);
  };

  const [videoFile, setVideoFile] = useState(null);

  const handleFileChange = (event) => {
    setVideoFile(event.target.files[0]);
  };

  return (
    <div className="App">
      <div className="navbar_div">
        <Navbar enable={enable} setEnable={setEnable} isOpen={isOpen} setOpen={setOpen} />
      </div>
      <div className='body'>
        <div className="left-part">
          <Vid />
          <Search />
          <Subtitle
            timestamp="11:32"
            subtitle="WTFlkvnk sdvnlk k lflkds vdsv ds vdsv dv ds gb d  d sf   dsvkldsnvdskvlkm WTFlkvnk sdvnlk k lflkds vdsv ds vdsv dv ds gb d  d sf   dsvkldsnvdskvlkm WTFlkvnk sdvnlk k lflkds vdsv ds vdsv dv ds gb d  d sf   dsvkldsnvdskvlkm WTFlkvnk sdvnlk k lflkds vdsv ds vdsv dv ds gb d  d sf   dsvkldsnvdskvlkm TFlkvnk sdvnlk k lflkds vdsv ds vdsv dv ds gb d  d sf   dsvkldsnvdskvlkm WTFlkvnk sdvnlk k lflkds vdsv ds vdsv dv ds gb d  d sf   dsvkldsnvdskvlkm WTFlkvnk sdvnlk k lflkds vdsv ds vdsv dv ds gb d  d sf   dsvkldsnvdskvlkm WTFlkvnk sdvnlk k lflkds vdsv ds vdsv dv ds gb d  d sf   dsvkldsnvdskvlkm TFlkvnk sdvnlk k lflkds vdsv ds vdsv dv ds gb d  d sf   dsvkldsnvdskvlkm WTFlkvnk sdvnlk k lflkds vdsv ds vdsv dv ds gb d  d sf   dsvkldsnvdskvlkm WTFlkvnk sdvnlk k lflkds vdsv ds vdsv dv ds gb d  d sf   dsvkldsnvdskvlkm WTFlkvnk sdvnlk k lflkds vdsv ds vdsv dv ds gb d  d sf   dsvkldsnvdskvlkm " />
        </div>
        <div className="right-part">
          <Vid_list thumbnail="thumbnail-url.jpg"
            title="Video Title"
            uploader="Name"
            views="1M views"
            time="31 Sept 2024"
          />
        </div>
      </div>
      {enable ?
        (<div className={`upload_window ${isOpen ? "open1" : "close1"}`}>
          <div className={`popup ${isOpen ? "open2" : "close2"}`}>
            <h2>Upload Video</h2>
            {/* <div className='file_area'>
              <div style={{ marginBottom: "10px" }}>
                <img src={`${process.env.PUBLIC_URL}/upload_icon.png`} style={{ width: "30px", height: "40px" }} alt="Icons Video File"></img>
              </div>
              Add a file +
              <div>
                <input type="file" accept="video/*" onChange={handleFileChange}/>
                {videoFile && (
                  <video controls>
                    <source src={URL.createObjectURL(videoFile)} type={videoFile.type} />
                    Your browser does not support the video tag.
                  </video>
                )}
              </div>
            </div>
            <div className='upload_wind_btn'>
              <button className="cancel_btn" onClick={handleClose}>cancel</button>
              <button className="upload_btn" onClick={handleClose}>Upload</button>
            </div> */}
            <div className="video-uploader">
              {/* Upload Icon and Label */}
              <div style={{ marginBottom: '10px' }}>
                <img
                  src={`${process.env.PUBLIC_URL}/upload_icon.png`}
                  style={{ width: '30px', height: '40px' }}
                  alt="Upload Video Icon"
                />
                <span style={{ marginLeft: '10px' }}>Add a file +</span>
              </div>

              {/* File Input */}
              <div>
                <input
                  type="file"
                  accept="video/*"
                  onChange={handleFileChange}
                  style={{ marginBottom: '10px' }}
                />
              </div>

              {/* Video Preview */}
              {videoFile && (
                <div style={{ marginTop: '10px' }}>
                  <video controls width="100%" style={{ maxWidth: '500px' }}>
                    <source src={URL.createObjectURL(videoFile)} type={videoFile.type} />
                    Your browser does not support the video tag.
                  </video>
                </div>
              )}
            </div>
          </div>
        </div>) : null}
    </div>
  );
}

export default App;
