import React from 'react';
import { useState, useEffect } from 'react';

function Vid({ selectedVideo, Title }) {

const [selectedSubtitle, setSubtitle] = useState([]);
const displayTitle = Title || "None selected";
useEffect(() => {
    // Fetch video URL from your backend
    const fetchVideo = async () => {
        try {
        const response = await axios.get('http://localhost:8000/api/get_video_url'); // Your API endpoint
        setSelectedVideo(response.data.video_url); // Adjust based on your API response
        } catch (error) {
        console.error('Error fetching video:', error);
        }
    };

    fetchVideo();
    }, []);

    return (
    <div className="App">
        {selectedVideo && <VideoPlayer selectedVideo={selectedVideo} />}
    </div>
    );

return (
<div className="main_vid">
<header className="title-playing" >{displayTitle}</header>
<div className="play_area me-2">
<video className="play_area" width="1000" height ="500" controls>
    {/* <source src={selectedVideo} type="video/mp4" /> */}
    {console.log(selectedVideo)}
    <source src={`${selectedVideo}`} type="video/mp4" />
    {selectedSubtitle && selectedSubtitle.map((subtitle, index) => (
    <track
        key={index}
        src={subtitle.subtitle_file_url}
        label={subtitle.language}
        kind="subtitles"
        srclang={subtitle.language}
        default={index === 0}
    />
    ))}
    Your browser does not support the video tag.
</video>
</div>
</div>
);
}

export default Vid;
