import React, { useState, useEffect } from "react";

function Subtitle({ subs }) {
    const [selectedLanguage, setSelectedLanguage] = useState("");
    const [subtitleArray, setSubtitleArray] = useState([]);

    useEffect(() => {
        if (subs && Object.keys(subs).length > 0) {
            // Set the default language to the first one available
            const firstLanguage = Object.keys(subs)[0];
            setSelectedLanguage(firstLanguage);
        }
    }, [subs]);

    useEffect(() => {
        // Update the subtitle array whenever the selected language changes
        if (selectedLanguage && subs[selectedLanguage]) {
            const subtitles = subs[selectedLanguage];
            const newSubtitleArray = Object.entries(subtitles).map(([timestamp, dialogue]) => ({
                timestamp,
                dialogue
            }));
            setSubtitleArray(newSubtitleArray);
        }
    }, [selectedLanguage, subs]);

    const handleLanguageChange = (event) => {
        setSelectedLanguage(event.target.value);
    };

    const handleTimestampClick = (timestamp) => {
        console.log(`Seeking to timestamp: ${timestamp}`);
    };

    if (!subs || typeof subs !== 'object' || Object.keys(subs).length === 0) {
        return <div>No subtitles available.</div>;
    }

    return (
        <div className="subtitle">
            <select value={selectedLanguage} onChange={handleLanguageChange}>
                {Object.keys(subs).map((language) => (
                    <option key={language} value={language}>
                        {language}
                    </option>
                ))}
            </select>
            <ul>
                {subtitleArray.map((subtitle, index) => (
                    <li key={index}>
                        <div className="dialogue">
                            <a
                                id="timestamp"
                                href="#"
                                onClick={() => handleTimestampClick(subtitle.timestamp)}>
                                {subtitle.timestamp}
                            </a>{" "}
                            &nbsp;&nbsp;â€¢ {subtitle.dialogue}
                        </div>
                    </li>
                ))}
            </ul>
        </div>
    );
}

export default Subtitle;
