import React, { useState, useEffect, useRef } from "react";
import axios from "axios";

function Vid({ selectedVideoID, Title, setSub }) {
    const [selectedVideo, setSelectedVideo] = useState('');
    const [subtitles, setSubtitles] = useState([]);
    const [loading, setLoading] = useState(false);
    const videoRef = useRef(null);
    const displayTitle = Title || "None selected";

    useEffect(() => {
        const fetchVideoData = async () => {
            if (!selectedVideoID) return;

            setLoading(true);
            try {
                const response = await axios.get(`http://localhost:8000/play/${selectedVideoID}/`);
                setSelectedVideo(response.data.video_url);
                const allSubtitles = {};
                const subtitleFiles = [];

                await Promise.all(
                    response.data.subtitles.map(async (subtitle) => {
                        try {
                            const subtitleResponse = await axios.get(`http://localhost:8000/subtitles/${selectedVideoID}/${subtitle.language}/`);
                            const base64Data = subtitleResponse.data.subtitle_file;

                            // Convert base64 to a Blob URL
                            const blob = new Blob([new Uint8Array(atob(base64Data).split("").map(c => c.charCodeAt(0)))], { type: 'application/x-subrip' });
                            const url = URL.createObjectURL(blob);

                            const jsonSubtitles = JSON.parse(subtitleResponse.data.subtitle);
                            allSubtitles[subtitle.language] = jsonSubtitles;
                            subtitleFiles.push({ language: subtitle.language, subtitle_file_url: url });
                        } catch (error) {
                            console.error(`Error fetching subtitle for language ${subtitle.language}:`, error);
                        }
                    })
                );

                setSub(allSubtitles);
                setSubtitles(subtitleFiles);
            } catch (error) {
                console.error("Error fetching video data:", error);
            } finally {
                setLoading(false);
            }
        };

        fetchVideoData();
    }, [selectedVideoID, setSub]);

    // Log the selectedVideo whenever it changes
    useEffect(() => {
        console.log(selectedVideo);
    }, [selectedVideo]);

    const handleSeek = (timestamp) => {
        if (videoRef.current) {
            videoRef.current.pause();
            videoRef.current.currentTime = timestamp;
            videoRef.current.play();
        }
    };

    const videoKey = selectedVideoID ? `video-${selectedVideoID}` : "";

    return (
        <div className="main_vid">
            <div className="play_area">
                {loading && <div className="loading-circle">Loading...</div>}
                <video
                    className="play_area"
                    width="1000"
                    height="500"
                    controls
                    key={videoKey}  
                    ref={videoRef}
                    preload="auto"
                    onLoadedMetadata={() => {
                        videoRef.current.controls = true;
                    }}
                    onError={(error) => console.error("Error loading video:", error)}
                >
                    {selectedVideo && <source src={selectedVideo} type="video/mp4" />}
                    {subtitles.map((subtitle, index) => (
                        <track
                            key={index}
                            src={subtitle.subtitle_file_url}
                            label={subtitle.language}
                            kind="subtitles"
                            srcLang={subtitle.language}
                            default={subtitle.language === "eng"}
                        />
                    ))}
                    Your browser does not support the video tag.
                </video>
            </div>
            <header className="title-playing">{displayTitle}</header>
        </div>
    );
}

export default Vid;
