import React from 'react';

function Vid({ selectedVideo, subtitles, title }) {
  return (
    <div className="main_vid">
      <header className="title-playing">{title || "None"}</header>
      <div className="play_area me-2">
        <video className="play_area" width="1000" controls>
          <source src={selectedVideo} type="video/mp4" />
          {subtitles.map((subtitle, index) => (
            <track
              key={index}
            //   src={subtitle.subtitle_file_url}  // Use the subtitle file URL
              label={subtitle.language}
              kind="subtitles"
              srclang={subtitle.language}
              default={index === 0}
            />
          ))}
          Your browser does not support the video tag.
        </video>
      </div>
    </div>
  );
}

export default Vid;
