import React, { useState, useEffect } from "react";
import axios from "axios";

function Vid({ selectedVideoID, Title }) {
const [selectedVideo, setSelectedVideo] = useState(null);
const [subtitles, setSubtitles] = useState([]);
const displayTitle = Title || "None selected";

useEffect(() => {
    const fetchVideoData = async () => {
    setSelectedVideo(null);
    setSubtitles([]);

    try {
        const response = await axios.get(
        `http://localhost:8000/play/${selectedVideoID}/`
        );
        setSelectedVideo(response.data.video_url);

        const subtitleTracks = response.data.subtitles.map((subtitle) => {
        const binaryStr = atob(subtitle.subtitle_file);
        const len = binaryStr.length;
        const bytes = new Uint8Array(len);
        for (let i = 0; i < len; i++) {
            bytes[i] = binaryStr.charCodeAt(i);
        }

        const decoder = new TextDecoder();
        const decodedSubtitle = decoder.decode(bytes);

        const blob = new Blob([decodedSubtitle], {
            type: "application/x-subrip",
        });
        return {
            language: subtitle.language,
            subtitle_file_url: URL.createObjectURL(blob),
        };
        });

        setSubtitles(subtitleTracks);
    } catch (error) {
        console.error("Error fetching video data:", error);
    }
    };

    if (selectedVideoID) {
    fetchVideoData();
    }
}, [selectedVideoID]);

// Generate a unique key based on selectedVideoID
const videoKey = selectedVideoID ? `video-${selectedVideoID}` : "";

return (
    <div className="main_vid">
    <div className="play_area">
        {console.log(selectedVideo)}
        {console.log(selectedVideoID)}
        <video
        className="play_area"
        width="1000"
        height="500"
        controls
        key={videoKey} // Key property to force re-render when video changes
        >
        {selectedVideo && <source src={selectedVideo} type="video/mp4" />}
        {subtitles.map((subtitle, index) => (
            <track
            key={index}
            src={subtitle.subtitle_file_url}
            label={subtitle.language}
            kind="subtitles"
            srcLang={subtitle.language}
            default={index === 0} // Make the first track the default one
            />
        ))}
        Your browser does not support the video tag.
        </video>
    </div>
    <header className="title-playing">{displayTitle}</header>
    </div>
);
}

export default Vid;
