        <div className="Search">
            <form className="d-flex" role="search">
                <input
                    className="form-control"
                    type="search"
                    placeholder="Search dialogue"
                    aria-label="Search"
                    value={searchText}
                    onChange={handleInputChange} // Track input changes
                />
            </form>
        </div>
    );
}

