import React, { useState, useEffect, useRef } from "react";
import axios from "axios";

function Vid({ selectedVideoID, Title, setSub }) {
    const [selectedVideo, setSelectedVideo] = useState(null);
    const [subtitles, setSubtitles] = useState([]);
    const [loading, setLoading] = useState(false);
    const videoRef = useRef(null);
    const displayTitle = Title || "None selected";

    useEffect(() => {
        const fetchVideoData = async () => {
            setLoading(true);

            // Clear previous video and subtitle state
            setSelectedVideo(null);
            setSubtitles([]);
            setSub({}); // Clear subtitles in parent

            try {
                const response = await axios.get(`http://localhost:8000/play/${selectedVideoID}/`);
                setSelectedVideo(response.data.video_url);

                const allSubtitles = {};
                const subtitleFiles = [];

                await Promise.all(
                    response.data.subtitles.map(async (subtitle) => {
                        try {
                            const subtitleResponse = await axios.get(`http://localhost:8000/subtitles/${selectedVideoID}/${subtitle.language}/`);
                            const base64Data = subtitleResponse.data.subtitle_file;
                            const binaryString = atob(base64Data);
                            const binaryLength = binaryString.length;
                            const bytes = new Uint8Array(binaryLength);
                            for (let i = 0; i < binaryLength; i++) {
                                bytes[i] = binaryString.charCodeAt(i);
                            }
                            const blob = new Blob([bytes], { type: 'application/x-subrip' });
                            const url = URL.createObjectURL(blob);
                            const jsonSubtitles = JSON.parse(subtitleResponse.data.subtitle);
                            allSubtitles[subtitle.language] = jsonSubtitles;
                            subtitleFiles.push({ language: subtitle.language, subtitle_file_url: url });
                        } catch (error) {
                            console.error(`Error fetching subtitle for language ${subtitle.language}:`, error);
                        }
                    })
                );

                setSub(allSubtitles); // Update subtitles in parent
                setSubtitles(subtitleFiles);
            } catch (error) {
                console.error("Error fetching video data:", error);
            } finally {
                setLoading(false);
            }
        };

        if (selectedVideoID) {
            fetchVideoData();
        }
    }, [selectedVideoID, setSub]);

    const handleSeek = (timestamp) => {
        if (videoRef.current) {
            videoRef.current.pause(); 
            videoRef.current.currentTime = timestamp; 
            videoRef.current.play(); 
        }
    };

    useEffect(() => {
        const handleSeeking = (event) => {
            console.log("Seeking to:", event.target.currentTime);
        };

        const handleSeeked = (event) => {
            console.log("Seeked to:", event.target.currentTime);
        };

        if (videoRef.current) {
            videoRef.current.addEventListener('seeking', handleSeeking);
            videoRef.current.addEventListener('seeked', handleSeeked);
        }

        // Cleanup the event listeners when the component unmounts or videoRef changes
        return () => {
            if (videoRef.current) {
                videoRef.current.removeEventListener('seeking', handleSeeking);
                videoRef.current.removeEventListener('seeked', handleSeeked);
            }
        };
    }, [videoRef]);

    const videoKey = selectedVideoID ? `video-${selectedVideoID}` : "";

    return (
        <div className="main_vid">
            <div className="play_area">
                {loading && <div className="loading-circle">Loading...</div>}
                <video
                    className="play_area"
                    width="1000"
                    height="500"
                    controls
                    key={videoKey}
                    ref={videoRef}
                    preload="auto"
                >
                    {selectedVideo && <source src={selectedVideo} type="video/mp4" />}
                    {subtitles.map((subtitle, index) => (
                        <track
                            key={index}
                            src={subtitle.subtitle_file_url}
                            label={subtitle.language}
                            kind="subtitles"
                            srcLang={subtitle.language}
                            default={subtitle.language === "eng"} // Set default if the language is English
                        />
                    ))}
                    Your browser does not support the video tag.
                </video>
            </div>
            <header className="title-playing">{displayTitle}</header>
        </div>
    );
}

export default Vid;
