import React from 'react';
import { useState, useEffect } from 'react';

function Vid({ selectedVideo, subtitles, title }) {
const [subtitleBlobs, setSubtitleBlobs] = useState([]);

useEffect(() => {
    if (subtitles) {
    const blobs = subtitles.map(subtitle => {
        // Decode Base64 to binary data
        const byteCharacters = atob(subtitle.subtitle_file);
        const byteNumbers = new Array(byteCharacters.length);
        for (let i = 0; i < byteCharacters.length; i++) {
        byteNumbers[i] = byteCharacters.charCodeAt(i);
        }
        const byteArray = new Uint8Array(byteNumbers);

        // Create a Blob and URL for each subtitle file
        const blob = new Blob([byteArray], { type: 'text/vtt' });
        const url = URL.createObjectURL(blob);
        return { ...subtitle, url };
    });

    setSubtitleBlobs(blobs);
    }
}, [subtitles]);

return (
    <div className="main_vid">
    <header className="title-playing">{title || "None"}</header>
    <div className="play_area me-2">
        <video className="play_area" width="1000" controls>
        <source src={selectedVideo} type="video/mp4" />
        {subtitleBlobs.map((subtitle, index) => (
            <track
            key={index}
            src={subtitle.url}
            label={subtitle.language}
            kind="subtitles"
            srclang={subtitle.language}
            default={index === 0}
            />
        ))}
        Your browser does not support the video tag.
        </video>
    </div>
    </div>
);
}

export default Vid;
