import './App.css';
import Vid from './video_section';
import Search from './search';
import Vid_list from './vid_list';
import Subtitle from './subtitle_section';
import Navbar from './Navbar';
import { useState, useEffect } from 'react';
import Upload_window from './upload_window'; 
import axios from 'axios';    
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom'; // Import Router components

function App() {
  const [enable, setEnable] = useState(false);
  const [isOpen, setOpen] = useState(false);
  const [videoFile, setVideoFile] = useState(null);
  const [uploader, setUploader] = useState("");
  const [latest_list, setLatestList] = useState([]);
  const [selectedVideoID, setSelectedVideoID] = useState(null);
  const [Title, setTitle] = useState(null);
  const [Uploadsuccess, setUploadsuccess] = useState(false);
  const [sub, setSub] = useState(null);

  // Function to close the popup
  const handleClose = () => {
    setOpen(false);
    setTimeout(() => {
      setEnable(false);
      setVideoFile(null);
      setUploader("");
    }, 500);
  };

  // Function to handle uploader input change
  const updateUploader = (e) => {
      setUploader(e.target.value);
  };

  // Function to handle file input change
  const handleFileChange = (event) => {
      setVideoFile(event.target.files[0]);
  };

  const fetchList = async () => {
      try {
          const response = await axios.get('http://localhost:8000/');
          setLatestList(response.data);
      } catch (error) {
          console.error("Error fetching video list:", error);
      }
  };

  useEffect(() => {
      fetchList();
  }, []);

  useEffect(() => {
    if (Uploadsuccess) {
      fetchList();
      setUploadsuccess(false);
    }
  }, [Uploadsuccess]);

  return (
    <Router>
      <div className="App">
        <div className="navbar_div">
          <Navbar setEnable={setEnable} setOpen={setOpen} />
        </div>
        <div className='body'>
          <div className="left-part">
            <Routes>
              <Route 
                path="/video/:id" 
                element={
                  <Vid 
                    selectedVideoID={selectedVideoID} 
                    Title={Title} 
                    setSub={setSub} 
                  />
                } 
              />
              <Route 
                path="/" 
                element={
                  <>
                    <Vid selectedVideoID={selectedVideoID} Title={Title} setSub={setSub}/>
                    <Search />
                    <Subtitle sub={sub} />
                  </>
                } 
              />
            </Routes>
          </div>
          <div className="right-part">
            <Vid_list latest_list={latest_list} setSelectedVideoID={setSelectedVideoID} setTitle={setTitle} setUploadsuccess={setUploadsuccess} />
          </div>
        </div>
        {enable ? (
          <Upload_window 
            isOpen={isOpen} 
            uploader={uploader} 
            videoFile={videoFile} 
            handleFileChange={handleFileChange}
            handleClose={handleClose} 
            updateUploader={updateUploader} 
            setUploadsuccess={setUploadsuccess} 
          />
        ) : null}
      </div>
    </Router>
  );
}

export default App;
