import React, { useState, useEffect } from "react";

function Subtitle({ sub }) {
const [parsedSubtitles, setParsedSubtitles] = useState([]);

useEffect(() => {

const parseSubtitles = (sub) => {
    const subtitleList = [];
    const regex = /(\d{2}:\d{2}.\d{3}) --> \d{2}:\d{2}.\d{3} (.+)/g;
    let match;

    while ((match = regex.exec(sub)) !== null) {
    const timestamp = match[1];
    const dialogue = match[2];
    subtitleList.push({ timestamp, dialogue });
    }

    return subtitleList;
};

const parsed = parseSubtitles(sub); 
setParsedSubtitles(parsed);
}, [sub]);

const handleTimestampClick = (timestamp) => {
console.log(`Jump to: ${timestamp}`);
};

return (
<div className="subtitle">
    <ul>
    {parsedSubtitles.map((subtitle, index) => (
        <li key={index}>
        <div className="dialogue">
            <a
            id="timestamp"
            href="#"
            onClick={() => handleTimestampClick(subtitle.timestamp)}>
            {subtitle.timestamp}
            </a>{" "}
            &nbsp;&nbsp;â€¢ {subtitle.dialogue}
        </div>
        </li>
    ))}
    </ul>
</div>
);
}

export default Subtitle;
