import Vid from './video_section';
import Search from './search';
import Vid_list from './vid_list';
import Subtitle from './subtitle_section';
import Navbar from './Navbar';
import { useEffect } from 'react';
import Upload_window from './upload_window'; 
import axios from 'axios';    

function Main({
    enable,
    setEnable,
    isOpen,
    setOpen,
    videoFile,
    setVideoFile,
    uploader,
    setUploader,
    latestList,
    setLatestList,
    selectedVideoID,
    setSelectedVideoID,
    title, // Fixed variable name to lowercase
    setTitle,
    uploadSuccess, // Fixed variable name to lowercase
    setUploadSuccess,
    sub,
    setSub,
    searchTerm,
    setSearchTerm
}) {

    const handleClose = () => {
        setOpen(false);
        setTimeout(() => {
            setEnable(false);
            setVideoFile(null);
            setUploader("");
        }, 500);
    };

    const updateUploader = (e) => {
        setUploader(e.target.value);
    };

    const handleFileChange = (event) => {
        setVideoFile(event.target.files[0]);
    };

    // Fetch latest video list
    const fetchList = async () => {
        try {
            const response = await axios.get("http://localhost:8000/");
            setLatestList(response.data);
        } catch (error) {
            console.error("Error fetching video list:", error);
        }
    };

    // Effect hooks
    useEffect(() => {
        fetchList();
    }, []);

    useEffect(() => {
        if (uploadSuccess) { // Fixed variable name to lowercase
            fetchList();
            setUploadSuccess(false);
        }
    }, [uploadSuccess]); // Fixed variable name to lowercase

    return (
        <div className="App">
            <div className="navbar_div">
                <Navbar setEnable={setEnable} setOpen={setOpen} />
            </div>
            <div className="body">
                <div className="left-part">
                    <Vid
                        selectedVideoID={selectedVideoID}
                        title={title} // Fixed variable name to lowercase
                        setSub={setSub}
                    />
                    {selectedVideoID && <Search setSearchTerm={setSearchTerm} />}
                    <Subtitle
                        sub={sub}
                        searchTerm={searchTerm}
                        selectedVideoID={selectedVideoID}
                    />
                </div>
                <div className="right-part">
                    <Vid_list
                        latestList={latestList}
                        setSelectedVideoID={setSelectedVideoID}
                        setTitle={setTitle}
                        setUploadSuccess={setUploadSuccess}
                    />
                </div>
            </div>
            {enable && (
                <Upload_window
                    isOpen={isOpen}
                    uploader={uploader}
                    videoFile={videoFile}
                    handleFileChange={handleFileChange}
                    handleClose={handleClose}
                    updateUploader={updateUploader}
                    setUploadSuccess={setUploadSuccess}
                />
            )}
        </div>
    );
}

export default Main;
