import React, { useState } from "react";

function Subtitle({ subs }) {
    const [selectedLanguage, setSelectedLanguage] = useState(Object.keys(subs)[0]);

    const handleLanguageChange = (event) => {
        setSelectedLanguage(event.target.value);
    };

    // Get the subtitles for the selected language
    const subtitles = subs[selectedLanguage] || {};

    const subtitleArray = Object.entries(subtitles).map(([timestamp, dialogue]) => ({
        timestamp,
        dialogue
    }));

    return (
        <div className="subtitle">
            {console.log(subs)}
            <select value={selectedLanguage} onChange={handleLanguageChange}>
                {Object.keys(subs).map((language) => (
                    <option key={language} value={language}>
                        {language}
                    </option>
                ))}
            </select>
            <ul>
                {subtitleArray.map((subtitle, index) => (
                    <li key={index}>
                        <div className="dialogue">
                            <a
                                id="timestamp"
                                href="#"
                                onClick={() => handleTimestampClick(subtitle.timestamp)}>
                                {subtitle.timestamp}
                            </a>{" "}
                            &nbsp;&nbsp;â€¢ {subtitle.dialogue}
                        </div>
                    </li>
                ))}
            </ul>
        </div>
    );
}

export default Subtitle;
