import './App.css';
import Vid from './video_section';
import Search from './search';
import Vid_list from './vid_list';
import Subtitle from './subtitle_section';
import Navbar from './Navbar';
import { useState } from 'react';
import Upload_window from './upload_window';

function App() {
  const [enable, setEnable] = useState(false);
  const [isOpen, setOpen] = useState(false);
  const [videoFile, setVideoFile] = useState(null);
  const [uploader, setUploader] = useState("");

  // Function to close the popup
  const handleClose = () => {
    setOpen(false);
    setTimeout(() => {
      setEnable(false);
      setVideoFile(null);
      setUploader("");
    }, 500);
  };

  // Function to handle uploader input change
  const updateUploader = (e) => {
      setUploader(e.target.value);
  };

  // Function to handle file input change
  const handleFileChange = (event) => {
      setVideoFile(event.target.files[0]);
  };

  // Function to handle form submit
  const handleSubmit = async (e) => {
      e.preventDefault();

      if (!videoFile || !uploader) {
      alert("Please select a video file and provide uploader name");
      return;
      }

      const formData = new FormData();
      formData.append("video", videoFile);
      formData.append("uploader", uploader);

      try {
      const response = await axios.post(
          "http://localhost:8000/upload/",
          formData,
          {
          headers: {
              "Content-Type": "multipart/form-data",
          },
          }
      );
      console.log(response);
      if (response.status === 200) {
          alert("Video uploaded successfully");
      } else {
          alert("Upload failed. Status: " + response.status);
      }
      } catch (error) {
      console.error("Error uploading video:", error);
      alert("Network error or server issue during upload");
      }
  };


  return (
    <div className="App">
      <div className="navbar_div">
        <Navbar setEnable={setEnable} setOpen={setOpen} />
      </div>
      <div className='body'>
        <div className="left-part">
          <Vid />
          <Search />
          <Subtitle timestamp="11:32" subtitle="" />
        </div>
        <div className="right-part">
          <Vid_list thumbnail="thumbnail-url.jpg" title="Video Title" uploader="Name" views="1M views" time="31 Sept 2024" />
        </div>
      </div>
      {enable ? (
        <Upload_window setEnable={setEnable} setOpen={setOpen} setUploader={setUploader} setVideoFile={setVideoFile} handleClose={handleClose} />
      ) : null}
    </div>
  );
}

export default App;
