import React, { useState, useEffect, useRef } from "react";
import axios from "axios";

function Vid({ selectedVideoID, Title, setSub }) {
    const [subtitles, setSubtitles] = useState([]);
    const [loading, setLoading] = useState(false);
    const videoRef = useRef(null);
    const displayTitle = Title || "None selected";

    useEffect(() => {
        const fetchVideoData = async () => {
            if (!selectedVideoID) return;

            setLoading(true);
            try {
                // Fetch video metadata and URL
                const response = await axios.get(`http://localhost:8000/play/${selectedVideoID}/`);
                const videoUrl = response.data.video_url;

                // Set the video source for streaming
                if (videoRef.current) {
                    videoRef.current.src = videoUrl; // Set the initial URL to start playing
                    videoRef.current.load(); // Load the new video source
                }

                const allSubtitles = {};
                const subtitleFiles = [];

                // Fetch subtitles
                await Promise.all(
                    response.data.subtitles.map(async (subtitle) => {
                        try {
                            const subtitleResponse = await axios.get(`http://localhost:8000/subtitles/${selectedVideoID}/${subtitle.language}/`);
                            const base64Data = subtitleResponse.data.subtitle_file;
                            const binaryString = atob(base64Data);
                            const binaryLength = binaryString.length;
                            const bytes = new Uint8Array(binaryLength);
                            for (let i = 0; i < binaryLength; i++) {
                                bytes[i] = binaryString.charCodeAt(i);
                            }
                            const blob = new Blob([bytes], { type: 'application/x-subrip' });
                            const url = URL.createObjectURL(blob);
                            allSubtitles[subtitle.language] = JSON.parse(subtitleResponse.data.subtitle);
                            subtitleFiles.push({ language: subtitle.language, subtitle_file_url: url });
                        } catch (error) {
                            console.error(`Error fetching subtitle for language ${subtitle.language}:`, error);
                        }
                    })
                );

                setSub(allSubtitles);
                setSubtitles(subtitleFiles);


                // Create a URL for the video blob and set it as the source
                const videoBlobUrl = URL.createObjectURL(streamResponse.data);
                videoRef.current.src = videoBlobUrl; // Set the video source to the blob URL
                videoRef.current.load(); // Load the new video source
            } catch (error) {
                console.error("Error fetching video data:", error);
            } finally {
                setLoading(false);
            }
        };

        fetchVideoData();
    }, [selectedVideoID, setSub]);

    const handleSeek = (timestamp) => {
        if (videoRef.current) {
            videoRef.current.pause();
            // Convert timestamp to seconds
            const timeParts = timestamp.split(':');
            if (timeParts.length === 3) {
                const seconds = (+timeParts[0] * 3600) + (+timeParts[1] * 60) + (+timeParts[2]);
                videoRef.current.currentTime = seconds; // Seek to the specific time
            } else {
                console.error("Invalid timestamp format.");
            }
            videoRef.current.play();
        }
    };

    return (
        <div className="main_vid">
            <div className="play_area">
                {loading && <div className="loading-circle">Loading...</div>}
                <video
                    className="play_area"
                    width="1000"
                    height="500"
                    controls
                    ref={videoRef}
                    preload="auto"
                    onLoadedMetadata={() => {
                        videoRef.current.controls = true;
                    }}
                    onError={(error) => console.error("Error loading video:", error)}
                >
                    {subtitles.map((subtitle, index) => (
                        <track
                            key={index}
                            src={subtitle.subtitle_file_url}
                            label={subtitle.language}
                            kind="subtitles"
                            srcLang={subtitle.language}
                            default={subtitle.language === "eng"}
                        />
                    ))}
                    Your browser does not support the video tag.
                </video>
            </div>
            <header className="title-playing">{displayTitle}</header>
        </div>
    );
}

export default Vid;
