import { useState } from 'react';
import axios from 'axios'; 

    function Upload_window({ setEnable, setOpen,handleClose }) {
    const [uploader, setUploader] = useState("");

    // Function to handle uploader input change
    const updateUploader = (e) => {
        setUploader(e.target.value);
    };

    // Function to handle file input change
    const handleFileChange = (event) => {
        setVideoFile(event.target.files[0]);
    };

    // Function to handle form submit
    const handleSubmit = async (e) => {
        e.preventDefault();

        if (!videoFile || !uploader) {
        alert("Please select a video file and provide uploader name");
        return;
        }

        const formData = new FormData();
        formData.append("video", videoFile);
        formData.append("uploader", uploader);

        try {
        const response = await axios.post(
            "http://localhost:8000/upload/",
            formData,
            {
            headers: {
                "Content-Type": "multipart/form-data",
            },
            }
        );
        console.log(response);
        if (response.status === 200) {
            alert("Video uploaded successfully");
        } else {
            alert("Upload failed. Status: " + response.status);
        }
        } catch (error) {
        console.error("Error uploading video:", error);
        alert("Network error or server issue during upload");
        }
    };

    return (
        <div className={`upload_window ${isOpen ? "open1" : "close1"}`}>
        <div className={`popup ${isOpen ? "open2" : "close2"}`}>
            <h2>Upload Video</h2>
            <div className="file_area">
            {videoFile ? (
                <div style={{ textAlign: "center" }}>
                <video controls style={{ width: "10%", maxWidth: "500px" }}>
                    <source
                    src={URL.createObjectURL(videoFile)}
                    type={videoFile.type}
                    />
                    Your browser does not support the video tag.
                </video>
                </div>
            ) : (
                <div style={{ textAlign: "center" }}>
                <img
                    src={`${process.env.PUBLIC_URL}/upload_icon.png`}
                    style={{ width: "30px", height: "40px" }}
                    alt="Icons Video File"
                />
                <p>Add a file +</p>
                </div>
            )}
            <div className="choose">
                <input
                className="vid_input"
                type="file"
                accept="video/*"
                onChange={handleFileChange}
                />
            </div>
            <input
                className="uploader"
                type="text"
                placeholder="Uploader Name"
                onChange={updateUploader}
                value={uploader}
            />
            </div>
            <div className="upload_wind_btn">
            <button className="cancel_btn" onClick={handleClose}>
                Cancel
            </button>
            <button className="upload_btn" onClick={handleSubmit}>
                Upload
            </button>
            </div>
        </div>
        </div>
    );
    }

    export default Upload_window;
