import React from 'react';

function Vid({ selectedVideo, subtitles ,title}) {
return (
    <div className="main_vid">
    <header className="title-playing">Golu</header>
    <div className="play_area me-2">
        <video className="play_area" width="1000" controls>
        <source src={selectedVideo} type="video/mp4" />
        {/* Loop through the subtitles and create a <track> for each */}
        {subtitles.map((subtitle, index) => (
            <track
            key={index}
            src={subtitle.subtitle_file}  // Subtitle file path
            label={subtitle.language}      // Language label
            kind="subtitles"
            srclang={subtitle.language}    // Language code
            default={index === 0}          // Set the first subtitle as default
            />
        ))}
        Your browser does not support the video tag.
        </video>
    </div>
    </div>
);
}

export default Vid;
