import React from 'react';

const Vid_list = ({ latest_list, setSelectedVideo, setSubtitle, setTitle }) => {
  const handleVideoSelect = (video) => {
    setSelectedVideo(video.vid_path); // Assuming vid_path is the video URL
    setSubtitle(video.subtitles);     // Assuming subtitles is an array
    setTitle(video.title);           // Assuming title is the video title
  };

  return (
    <div className="list_view">
      {latest_list.map((video, index) => (
        <div key={index} className="Card" onClick={() => handleVideoSelect(video)}>
          <div className="thumbnail_block">
            <img src={video.thumbnail} alt="thumbnail" className="thumbnail" />
          </div>
          <div className="video-info">
            <h4 id="recom_vid_title">{video.title}</h4>
            <p className="uploader">Uploader - {video.uploader}</p>
            <p>Uploaded on - {new Date(video.created_at).toLocaleDateString("en-GB")}</p>
            <p>{video.vid_path}</p>
          </div>
        </div>
      ))}
    </div>
  );
};

export default Vid_list;
