import React, { useState, useEffect } from "react";
import axios from "axios";

function Vid({ selectedVideoID, Title }) {
const [selectedVideo, setSelectedVideo] = useState(null);
const [selectedSubtitle, setSubtitle] = useState([]);
const displayTitle = Title || "None selected";

useEffect(() => {
    const fetchVideoData = async () => {
    try {
        const response = await axios.get(
        `http://localhost:8000/play/${selectedVideoID}/`
        );
        setSelectedVideo(response.data.video_url);

        // Decode base64 subtitles and create Blob URLs
        const subtitles = response.data.subtitles.map((subtitle) => {
        const decodedSubtitle = atob(subtitle.subtitle_file);
        const blob = new Blob([decodedSubtitle], {
            type: "application/x-subrip",
        });
        return {
            language: subtitle.language,
            subtitle_file_url: URL.createObjectURL(blob),
        };
        });

        setSubtitle(subtitles);
    } catch (error) {
        console.error("Error fetching video data:", error);
    }
    };

    if (selectedVideoID) {
    fetchVideoData();
    }
}, [selectedVideoID]);

