import React from "react";

function Subtitle({ sub }) {
    const handleTimestampClick = (timestamp) => {
        // Logic to seek video to the specified timestamp
        console.log(`Seeking to timestamp: ${timestamp}`);
    };

    // Check if sub is valid before processing
    if (!sub) {
        return <div>No subtitles available.</div>; // Handle the case where sub is null or undefined
    }

    // Parse the JSON string if it's a string
    let subtitleData;
    try {
        subtitleData = typeof sub === 'string' ? JSON.parse(sub) : sub;
    } catch (error) {
        return <div>Error parsing subtitles.</div>; // Handle JSON parsing error
    }

    // Convert the JSON object into an array of { timestamp, dialogue } pairs
    const subtitleArray = Object.entries(subtitleData).map(([timestamp, dialogue]) => ({
        timestamp,
        dialogue
    }));

    return (
        <div className="subtitle">
            {console.log(subtitleArray)}
            <ul>
                {subtitleArray.map((subtitle, index) => (
                    <li key={index}>
                        <div className="dialogue">
                            <a
                                id="timestamp"
                                href="#"
                                onClick={() => handleTimestampClick(subtitle.timestamp)}>
                                {subtitle.timestamp}
                            </a>{" "}
                            &nbsp;&nbsp;â€¢ {subtitle.dialogue}
                        </div>
                    </li>
                ))}
            </ul>
        </div>
    );
}

export default Subtitle;
