import React, { useState, useEffect } from "react";
import axios from "axios";

function Vid({ selectedVideoID, Title, setSub }) {
    const [selectedVideo, setSelectedVideo] = useState(null);
    const [subtitles, setSubtitles] = useState([]);
    const displayTitle = Title || "None selected";

    useEffect(() => {
        const fetchVideoData = async () => {
            setSelectedVideo(null);
            setSubtitles([]);

            try {
                const response = await axios.get(`http://localhost:8000/play/${selectedVideoID}/`);
                setSelectedVideo(response.data.video_url);

                const subtitleTracks = await Promise.all(
                    response.data.subtitles.map(async (subtitle) => {
                        try {
                            const subtitleResponse = await axios.get(`http://localhost:8000/api/subtitles/${selectedVideoID}/${subtitle.language}/`);
                            const base64Data = subtitleResponse.data.subtitle_file;

                            const binaryString = atob(base64Data);
                            const binaryLength = binaryString.length;
                            const bytes = new Uint8Array(binaryLength);
                            for (let i = 0; i < binaryLength; i++) {
                                bytes[i] = binaryString.charCodeAt(i);
                            }

                            const decoder = new TextDecoder('utf-8');
                            const decodedText = decoder.decode(bytes);

                            const blob = new Blob([bytes], { type: 'application/x-subrip' });
                            const url = URL.createObjectURL(blob);

                            // Convert the subtitle JSON into an array if needed
                            const jsonSubtitles = JSON.parse(subtitleResponse.data.subtitle);
                            const subtitleArray = Object.entries(jsonSubtitles).map(([timestamp, dialogue]) => ({
                                timestamp,
                                dialogue,
                            }));

                            return {
                                language: subtitle.language,
                                subtitle_file_url: url,
                                subtitleArray, // Store the array of subtitle objects
                                decodedText,   // Store the decoded subtitle text
                            };
                        } catch (error) {
                            console.error(`Error fetching subtitle for language ${subtitle.language}:`, error);
                            return null;
                        }
                    })
                );

                const validTracks = subtitleTracks.filter(track => track !== null);
                setSubtitles(validTracks);

                // Set subtitles once all tracks have been processed
                if (validTracks.length > 0) {
                    const allSubtitles = validTracks.map(track => track.decodedText).join("\n");
                    setSub(allSubtitles); // Set combined subtitles
                }
            } catch (error) {
                console.error("Error fetching video data:", error);
            }
        };

        if (selectedVideoID) {
            fetchVideoData();
        }
    }, [selectedVideoID, setSub]);

    const videoKey = selectedVideoID ? `video-${selectedVideoID}` : "";

    return (
        <div className="main_vid">
            <div className="play_area">
                <video className="play_area" width="1000" height="500" controls key={videoKey}>
                    {selectedVideo && <source src={selectedVideo} type="video/mp4" />}
                    {subtitles.map((subtitle, index) => (
                        <track
                            key={index}
                            src={subtitle.subtitle_file_url}
                            label={subtitle.language}
                            kind="subtitles"
                            srcLang={subtitle.language}
                            default={index === 0}
                        />
                    ))}
                    Your browser does not support the video tag.
                </video>
            </div>
            <header className="title-playing">{displayTitle}</header>
        </div>
    );
}

export default Vid;
