import React, { useState, useEffect, useRef } from "react";
import axios from "axios";
import ReactPlayer from 'react-player';

function Vid({ selectedVideoID, Title, setSub, seconds, setSeconds }) {
    const [subtitles, setSubtitles] = useState([]);
    const [loading, setLoading] = useState(false);
    const [videoUrl, setVideoUrl] = useState('');
    const playerRef = useRef(null);
    const displayTitle = Title || "None selected";

    useEffect(() => {
        const fetchVideoData = async () => {
            if (!selectedVideoID) return;

            setLoading(true);
            try {
                const response = await axios.get(`http://localhost:8000/play/${selectedVideoID}/`);
                const fetchedVideoUrl = response.data.video_url;

                setVideoUrl(fetchedVideoUrl);

                // Load subtitles
                await loadSubtitles(response.data.subtitles);

                // If seconds are provided, seek to that position
                if (seconds) {
                    setSeconds(null); // Reset seconds
                }
            } catch (error) {
                console.error("Error fetching video data:", error);
            } finally {
                setLoading(false);
            }
        };

        fetchVideoData();
    }, [selectedVideoID, setSub, seconds]);

    const loadSubtitles = async (subtitlesArray) => {
        // ... (existing loadSubtitles function)
    };

    const handleReady = () => {
        if (seconds) {
            playerRef.current.seekTo(seconds); // This should work correctly now
        }
    };

    return (
        <div className="main_vid">
            <div className="play_area">
                {loading && <div className="loading-circle">Loading...</div>}
                <ReactPlayer
                    ref={playerRef}
                    url={videoUrl}
                    width="1000px"
                    height="500px"
                    controls
                    onReady={handleReady}
                />
            </div>
            <header className="title-playing">{displayTitle}</header>
        </div>
    );
}

export default Vid;
