import React, { useState } from "react";

const Vid_list = ({ latest_list }) => {
const [selectedVideo, setSelectedVideo] = useState(null);

const formatDate = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleDateString("en-GB");
};

const handleVideoSelect = (video) => {
    setSelectedVideo(video.vid_path); // Set the selected video path
};

return (
    <div className="list_view">
    {selectedVideo && (
        <div className="video-player">
        <h4>Now Playing:</h4>
        <video width="600" controls>
            <source src={selectedVideo} type="video/mp4" />
            Your browser does not support the video tag.
        </video>
        </div>
    )}

    {latest_list.map((video, index) => (
        <div
        key={index}
        className="Card"
        onClick={() => handleVideoSelect(video)}>
        <div className="thumbnail_block">
            <img src={video.thumbnail} alt="thumbnail" className="thumbnail" />
        </div>
        <div className="video-info">
            <h4 id="recom_vid_title">{video.title}</h4>
            <p className="uploader">Uploader - {video.uploader}</p>
            <p className="uploader">
            Uploaded on - {formatDate(video.created_at)}
            </p>
            <p>{video.vid_path}</p>
        </div>
        </div>
    ))}
    </div>
);
};

export default Vid_list;
