import React, { useState, useEffect, useRef } from "react";
import axios from "axios";

function Vid({ selectedVideoID, Title, setSub, seconds, setSeconds }) {
    const [subtitles, setSubtitles] = useState([]);
    const [loading, setLoading] = useState(false);
    const videoRef = useRef(null);
    const displayTitle = Title || "None selected";

    useEffect(() => {
        const fetchVideoData = async () => {
            if (!selectedVideoID) return;

            setLoading(true);
            try {
                const response = await axios.get(`http://localhost:8000/play/${selectedVideoID}/`);
                const videoUrl = response.data.video_url;

                // Load subtitles
                await loadSubtitles(response.data.subtitles);

                // Fetch video portion if seconds are provided
                if (seconds) {
                    await fetchVideoStream(seconds);
                } else {
                    // If no seconds, just set the video URL
                    if (videoRef.current && videoRef.current.src !== videoUrl) {
                        videoRef.current.src = videoUrl;
                        videoRef.current.load();
                    }
                }
            } catch (error) {
                console.error("Error fetching video data:", error);
            } finally {
                setLoading(false);
            }
        };

        fetchVideoData();

        return () => {
            if (videoRef.current && videoRef.current.src) {
                URL.revokeObjectURL(videoRef.current.src);
            }
        };
    }, [selectedVideoID, setSub, seconds]);

    const loadSubtitles = async (subtitlesArray) => {
        const allSubtitles = {};
        const subtitleFiles = [];

        await Promise.all(
            subtitlesArray.map(async (subtitle) => {
                try {
                    const subtitleResponse = await axios.get(`http://localhost:8000/subtitles/${selectedVideoID}/${subtitle.language}/`);
                    const base64Data = subtitleResponse.data.subtitle_file;
                    const binaryString = atob(base64Data);
                    const binaryLength = binaryString.length;
                    const bytes = new Uint8Array(binaryLength);
                    for (let i = 0; i < binaryLength; i++) {
                        bytes[i] = binaryString.charCodeAt(i);
                    }
                    const blob = new Blob([bytes], { type: 'application/x-subrip' });
                    const url = URL.createObjectURL(blob);
                    allSubtitles[subtitle.language] = JSON.parse(subtitleResponse.data.subtitle);
                    subtitleFiles.push({ language: subtitle.language, subtitle_file_url: url });
                } catch (error) {
                    console.error(`Error fetching subtitle for language ${subtitle.language}:`, error);
                }
            })
        );

        setSub(allSubtitles);
        setSubtitles(subtitleFiles);
    };

    const fetchVideoStream = async (seconds) => {
        if (loading) return; // Prevent multiple concurrent fetches

        const byteRange = calculateByteRange(seconds);
        try {
            const streamResponse = await axios.post(`http://localhost:8000/stream/${selectedVideoID}/`, null, {
                headers: { 'http_range': byteRange },
                responseType: 'blob'
            });

            const videoBlobUrl = URL.createObjectURL(streamResponse.data);
            videoRef.current.pause(); // Pause before changing source
            videoRef.current.src = videoBlobUrl; // Set the blob URL
            videoRef.current.currentTime = seconds; // Seek to the requested seconds
            videoRef.current.play(); // Start playback
            setSeconds(null); // Reset seconds
        } catch (error) {
            console.error("Error fetching video stream:", error);
        }
    };

    const calculateByteRange = (seconds) => {
        const bytesPerSecond = 1024; // Adjust based on your video's bitrate
        const startByte = seconds * bytesPerSecond;
        const endByte = startByte + bytesPerSecond - 1; // Request the next second of video
        return `bytes=${startByte}-${endByte}`;
    };

    return (
        <div className="main_vid">
            <div className="play_area">
                {loading && <div className="loading-circle">Loading...</div>}
                <video
                    className="play_area"
                    width="1000"
                    height="500"
                    controls
                    ref={videoRef}
                    preload="auto"
                    onLoadedMetadata={() => {
                        videoRef.current.controls = true;
                    }}
                    onError={(error) => console.error("Error loading video:", error)}
                >
                    {subtitles.map((subtitle, index) => (
                        <track
                            key={index}
                            src={subtitle.subtitle_file_url}
                            label={subtitle.language}
                            kind="subtitles"
                            srcLang={subtitle.language}
                            default={subtitle.language === "eng"}
                        />
                    ))}
                    Your browser does not support the video tag.
                </video>
            </div>
            <header className="title-playing">{displayTitle}</header>
        </div>
    );
}

export default Vid;
